// ToDoList.js
import React, { useReducer, useEffect, useState } from 'react';
import TodoCard from './TodoCard';
import Sidebar from './Sidebar';
import UserDropdown from './UserDropdown';
import NewTaskForm from './NewTaskForm'; 
import './StickyNotes.css'; 
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbtack, faPlus } from '@fortawesome/free-solid-svg-icons'; 
import TodoImg from '../images/Todoimg.png';

const ToDoList = () => {
    const initialState = {
        input: '',
        category: '', 
        deadline: '', 
        priority: '', 
        users: [],
        userTasks: []
    };

    const reducer = (state, action) => {
        switch (action.type) {
            case 'ADD_TASK':
                return {
                    ...state,
                    userTasks: [...state.userTasks, action.payload]
                };
            case 'REMOVE_TASK':
                return {
                    ...state,
                    userTasks: state.userTasks.filter(task => task.id !== action.payload)
                };
            case 'EDIT_TASK':
                return {
                    ...state,
                    userTasks: state.userTasks.map(task =>
                        task.id === action.payload.id ? action.payload : task
                    )
                };
            case 'SET_USERS':
                return {
                    ...state,
                    users: action.payload
                };
            case 'SET_USER_TASKS':
                return {
                    ...state,
                    userTasks: action.payload
                };
            case 'TOGGLE_TASK':
                return {
                    ...state,
                    userTasks: state.userTasks.map(task =>
                        task.id === action.payload ? { ...task, completed: !task.completed } : task
                    )
                };
            default:
                return state;
        }
    };

    const [state, dispatch] = useReducer(reducer, initialState);
    const [input, setInput] = useState(initialState.input);
    const [selectedUser, setSelectedUser] = useState('');
    const [editTask, setEditTask] = useState(null);
    const [filter, setFilter] = useState('ALL');
    const [priorityFilter, setPriorityFilter] = useState('ALL');
    const [searchQuery, setSearchQuery] = useState('');
    const [categories, setCategories] = useState([]);
    const [userCounts, setUserCounts] = useState({});
    const [sortOrder, setSortOrder] = useState('NONE'); 
    const [deadline, setDeadline] = useState(''); 
    const [priority, setPriority] = useState(''); 
    const [category, setCategory] = useState(''); 
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        fetch('http://localhost:8083/api/users')
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'SET_USERS', payload: data });
            })
            .catch(error => console.error('Error fetching users:', error));

        fetch('http://localhost:8083/api/categories')
            .then(response => response.json())
            .then(data => {
                setCategories(data);
            })
            .catch(error => console.error('Error fetching categories:', error));
    }, []);

    useEffect(() => {
        if (selectedUser) {
            fetch(`http://localhost:8083/api/todos/byuser/${selectedUser}`)
                .then(response => response.json())
                .then(data => {
                    dispatch({ type: 'SET_USER_TASKS', payload: data });
                    calculateCounts(data);
                })
                .catch(err => {
                    console.error('Error fetching todos:', err);
                });
        } else {
            dispatch({ type: 'SET_USER_TASKS', payload: [] });
            calculateCounts([]);
        }
    }, [selectedUser]);

    const handleInputChange = (e) => {
        setInput(e.target.value);
    };

    const handleCategoryChange = (e) => {
        setCategory(e.target.value);
    };

    const handleDeadlineChange = (e) => {
        setDeadline(e.target.value);
    };

    const handlePriorityChange = (e) => {
        setPriority(e.target.value);
    };

    const calculateCounts = (todos) => {
        const counts = {
            completed: 0,
            pending: 0,
            high: 0,
            medium: 0,
            low: 0,
            personal: 0,
            household: 0,
            financial: 0,
            work: 0,
            errand: 0,
            help: 0,
            total: 0
        };

        todos.forEach(todo => {
            if (todo.completed) {
                counts.completed++;
            } else {
                counts.pending++;
            }

            switch (todo.priority.toLowerCase()) {
                case 'high':
                    counts.high++;
                    break;
                case 'medium':
                    counts.medium++;
                    break;
                case 'low':
                    counts.low++;
                    break;
                default:
                    break;
            }

            switch (todo.category.toLowerCase()) {
                case 'personal task':
                    counts.personal++;
                    break;
                case 'household task':
                    counts.household++;
                    break;
                case 'financial task':
                    counts.financial++;
                    break;
                case 'work task':
                    counts.work++;
                    break;
                case 'errand':
                    counts.errand++;
                    break;
                case 'help others':
                    counts.help++;
                    break;
                default:
                    break;
            }

            counts.total++;
        });

        setUserCounts(counts);
    };

    const handleAdd = (task) => {
        if (!task.description) return;

        fetch('http://localhost:8083/api/todos', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(task)
        })
            .then(response => response.json())
            .then(newTask => {
                dispatch({ type: 'ADD_TASK', payload: newTask });
                setShowForm(false); 
                calculateCounts([...state.userTasks, newTask]);
            })
            .catch(err => {
                console.error('Error adding todo:', err);
            });
    };

    const handleRemove = (id) => {
        fetch(`http://localhost:8083/api/todos/${id}`, {
            method: 'DELETE'
        })
            .then(response => {
                if (response.ok) {
                    dispatch({ type: 'REMOVE_TASK', payload: id });
                    calculateCounts(state.userTasks.filter(task => task.id !== id));
                } else {
                    console.error('Failed to delete todo:', response.status);
                }
            })
            .catch(err => {
                console.error('Error deleting todo:', err);
            });
    };

    const handleEdit = (task) => {
        setEditTask(task);
    };

    const handleToggle = (id) => {
        fetch(`http://localhost:8083/api/todos/toggle/${id}`, {
            method: 'PUT'
        })
            .then(response => response.json())
            .then(updatedTask => {
                dispatch({ type: 'TOGGLE_TASK', payload: id });
                calculateCounts(state.userTasks.map(task => 
                    task.id === id ? { ...task, completed: !task.completed } : task
                ));
            })
            .catch(err => {
                console.error('Error toggling todo:', err);
            });
    };

    const handleSaveEdit = (updatedTask) => {
        fetch(`http://localhost:8083/api/todos/${updatedTask.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(updatedTask)
        })
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'EDIT_TASK', payload: data });
                setEditTask(null);
                calculateCounts(state.userTasks.map(task => 
                    task.id === data.id ? data : task
                ));
            })
            .catch(err => {
                console.error('Error updating todo:', err);
            });
    };

    const handleCancelEdit = () => {
        setEditTask(null);
    };

    const filteredTasks = state.userTasks
        .filter(task => {
            if (filter === 'ALL') return true;
            return filter === 'COMPLETED' ? task.completed : !task.completed;
        })
        .filter(task => {
            if (priorityFilter === 'ALL') return true;
            return task.priority === priorityFilter;
        })
        .filter(task => task.description.toLowerCase().includes(searchQuery.toLowerCase()));

    const sortedTasks = filteredTasks.sort((a, b) => {
        if (sortOrder === 'ASC') {
            return a.description.localeCompare(b.description);
        } else if (sortOrder === 'DESC') {
            return b.description.localeCompare(a.description);
        }
        return 0;
    });

    return (
        <div className="container-fluid">
            <div className="row">
                <div className="col-md-2">
                    <Sidebar userCounts={userCounts} categories={categories} />
                </div>
                <div className="col-md-10">
                    <UserDropdown
                        users={state.users}
                        selectedUser={selectedUser}
                        setSelectedUser={setSelectedUser}
                        setShowForm={setShowForm}
                    />
                    <div className="header">
                        <div className="pinned-tasks-header">
                            <h1>Task list <FontAwesomeIcon icon={faThumbtack} /></h1>
                        </div>
                        <div className="task-count">
                            <p>Total Tasks: {userCounts.total}</p>
                            <p>Completed Tasks: {userCounts.completed}</p>
                            <p>Pending Tasks: {userCounts.pending}</p>
                        </div>
                        <div className="header-actions">
                            <button className="btn btn-primary" onClick={() => setShowForm(true)}><FontAwesomeIcon icon={faPlus} /> Add Task</button>
                        </div>
                    </div>
                    {showForm && (
                        <NewTaskForm
                            handleAdd={handleAdd}
                            handleCancel={() => setShowForm(false)}
                            users={state.users}
                        />
                    )}
                    {editTask && (
                        <NewTaskForm
                            task={editTask}
                            handleAdd={handleSaveEdit}
                            handleCancel={handleCancelEdit}
                            users={state.users}
                        />
                    )}
                    <div className="card-columns">
                        {sortedTasks.map((task) => (
                            <TodoCard
                                key={task.id}
                                task={task}
                                handleToggle={handleToggle}
                                handleRemove={handleRemove}
                                handleEdit={handleEdit}
                            />
                        ))}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ToDoList;
