import React, { useReducer, useEffect, useState } from 'react';
import ToDo from './Todo';

// Reducer function to manage state transitions
const reducer = (state, action) => {
    switch (action.type) {
        case 'ADD_TASK':
            return {
                ...state,
                tasks: [...state.tasks, { taskName: action.payload, completed: false }]
            };
        case 'REMOVE_TASK':
            return {
                ...state,
                tasks: state.tasks.filter((_, index) => index !== action.payload)
            };
        case 'TOGGLE_TASK':
            return {
                ...state,
                tasks: state.tasks.map((task, index) =>
                    index === action.payload ? { ...task, completed: !task.completed } : task
                )
            };
        case 'RESET_TASKS':
            return {
                ...state,
                tasks: []
            };
        case 'SET_USERS':
            return {
                ...state,
                users: action.payload
            };
        default:
            return state;
    }
};

const ToDoList = () => {
    const initialState = {
        input: '',
        tasks: JSON.parse(localStorage.getItem('tasks')) || [],
        users: [] // New state to hold fetched usernames
    };

    const [state, dispatch] = useReducer(reducer, initialState);
    const [input, setInput] = useState(initialState.input);

    useEffect(() => {
        // Fetch usernames from API
        fetch('http://localhost:8083/api/users')
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'SET_USERS', payload: data });
            })
            .catch(error => console.error('Error fetching users:', error));
    }, []);

    useEffect(() => {
        localStorage.setItem('tasks', JSON.stringify(state.tasks));
    }, [state.tasks]);

    const handleAdd = () => {
        if (!input) return;
        dispatch({ type: 'ADD_TASK', payload: input });
        setInput('');
    };

    const handleRemove = (index) => {
        dispatch({ type: 'REMOVE_TASK', payload: index });
    };

    const handleToggle = (index) => {
        dispatch({ type: 'TOGGLE_TASK', payload: index });
    };

    const handleReset = () => {
        dispatch({ type: 'RESET_TASKS' });
    };

    return (
        <div>
            <h1>TODO List</h1>
            <h3>Add Task below</h3>
            <input value={input} onChange={(e) => setInput(e.target.value)} />
            <button onClick={handleAdd}>Add</button>
            {state.tasks.length > 0 && <button onClick={handleReset}>Reset</button>}
            <select>
                <option value="">Select User</option>
                {state.users.map((user, index) => (
                    <option key={index} value={user}>{user}</option>
                ))}
            </select>
            <ul>
                {state.tasks.map((task, index) => (
                    <ToDo
                        key={index}
                        index={index}
                        task={task}
                        handleToggle={handleToggle}
                        handleRemove={handleRemove}
                    />
                ))}
            </ul>
        </div>
    );
};

export default ToDoList;
