import React from 'react';

const ToDo = ({ index, task, handleToggle, handleRemove }) => {
    return (
        <li>
            <span
                style={{ textDecoration: task.completed ? 'line-through' : 'none' }}
                onClick={() => handleToggle(index)}
            >
                {task.taskName}
            </span>
            <button onClick={() => handleRemove(index)}>Remove</button>
        </li>
    );
};

export default ToDo;
