import React, { useEffect, useState } from 'react';
import 'bootstrap-icons/font/bootstrap-icons.css';

const Sidebar = ({ selectedUser }) => {
  const [categories, setCategories] = useState([]);
  const [counts, setCounts] = useState({
    completed: 0,
    pending: 0,
    high: 0,
    medium: 0,
    low: 0,
    personal: 0,
    household: 0,
    financial: 0,
    work: 0,
    errand: 0,
    help: 0,
    total: 0
  });

  useEffect(() => {
    if (selectedUser) {
      fetchCategories();
    }
  }, [selectedUser]);

  const fetchCategories = async () => {
    try {
      const categoriesResponse = await fetch(`http://localhost:8083/api/categories/user/${selectedUser}`);
      const categoriesData = await categoriesResponse.json();
      setCategories(categoriesData);

      const todosResponse = await fetch(`http://localhost:8083/api/todos/user/${selectedUser}`);
      const todosData = await todosResponse.json();
      calculateCounts(todosData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const calculateCounts = (todos) => {
    const counts = {
      completed: 0,
      pending: 0,
      high: 0,
      medium: 0,
      low: 0,
      personal: 0,
      household: 0,
      financial: 0,
      work: 0,
      errand: 0,
      help: 0,
      total: 0
    };

    todos.forEach(todo => {
      if (todo.completed) {
        counts.completed++;
      } else {
        counts.pending++;
      }

      switch (todo.priority.toLowerCase()) {
        case 'high':
          counts.high++;
          break;
        case 'medium':
          counts.medium++;
          break;
        case 'low':
          counts.low++;
          break;
        default:
          break;
      }

      switch (todo.category.toLowerCase()) {
        case 'personal task':
          counts.personal++;
          break;
        case 'household task':
          counts.household++;
          break;
        case 'financial task':
          counts.financial++;
          break;
        case 'work task':
          counts.work++;
          break;
        case 'errand':
          counts.errand++;
          break;
        case 'help others':
          counts.help++;
          break;
        default:
          break;
      }

      counts.total++;
    });

    setCounts(counts);
  };

  return (
    <div className="sidenav">
      {/* Sidebar content */}
    </div>
  );
};

export default Sidebar;
