import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from '../images/logo1.png'; //logo
import loginpic1 from '../images/loginpic1.svg'; 
import '../App.css';

const Login = () => {
  const [users, setUsers] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState('');
  const [password, setPassword] = useState(''); // State for password verification
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:8083/api/users")
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const validateForm = (event) => {
    event.preventDefault();
    const selectedUserName = users.find(user => user.id === selectedUserId)?.name;

    if (selectedUserId && password === localStorage.getItem(selectedUserId)) { // Check if password matches stored password
      alert(`Login successful! Welcome, ${selectedUserName}!`);
      navigate(`/dashboard`);
    } else {
      alert("Invalid user or password.");
    }
  };

  return (
    <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center">
      <nav className="navbar1 w-100">
        {/* Navbar code */}
      </nav>
      <div className="container">
        {/* Login form code */}
        <div className="col-md-6">
          <div className="col-md-10 mx-auto rounded shadow bg-white">
            <div className="row">
              <div className="col-md-12">
                <div className="m-5 text-center">
                  <form className="m-5" onSubmit={validateForm}>
                    <h2 style={{ textAlign: 'left' }}>Login</h2>
                    {/* User select dropdown */}
                    {/* Password verification input field */}
                    <div className="form-group row">
                      <label htmlFor="password" className="col-sm-2 col-form-label">Password</label>
                      <div className="col-sm-10">
                        <input
                          type="password"
                          className="form-control"
                          id="password"
                          value={password}
                          onChange={(e) => setPassword(e.target.value)}
                          required
                        />
                      </div>
                    </div>
                    <button type="submit" className="form-control btn btn-dark">Login</button>
                  </form>
                  {/* Register link */}
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <footer className="w-100 text-center mt-auto">
        {/* Footer code */}
      </footer>
    </div>
  );
};

export default Login;
