// src/Components/Login.js
import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from '../images/logo1.png'; //logo
import '../App.css';

const Login = () => {
  const [users, setUsers] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState('');
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:8083/api/users")
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const validateForm = (event) => {
    event.preventDefault();
    const selectedUserName = users.find(user => user.id === selectedUserId)?.name;

    if (selectedUserId) {
      alert(`Login successful! Welcome, ${selectedUserName}!`);
      navigate(`/dashboard`);
    } else {
      alert("Please select a user.");
    }
  };

  return (
    <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center">
      <nav className="navbar1 w-100">
        <nav className="navbar navbar-expand-lg navbar-light">
          <a className="navbar-brand" href="#">
          <img src={logo} alt="Organize" className="img-fluid" width="100" height="60" />
            Stay Organized
          </a>
          <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
            <span className="navbar-toggler-icon"></span>
          </button>
          <div className="collapse navbar-collapse" id="navbarNav">
            <ul className="navbar-nav">
              <li className="nav-item">
                <a className="nav-link" href="/">Home</a>
              </li>
              <li className="nav-item">
                <a className="nav-link" href="/register">Register</a>
              </li>
            </ul>
          </div>
        </nav>
      </nav>
      <div className="container">
        <div className="row">
          <div className="col-md-6">
            <div className="mt-5">
              <img src="images/undraw_access_account_re_8spm.svg" alt="login" className="img-fluid p-5" />
            </div>
          </div>
          <div className="col-md-6">
            <div className="col-md-10 mx-auto rounded shadow bg-white">
              <div className="row">
                <div className="col-md-12">
                  <div className="m-5 text-center">
                    <form className="m-5" onSubmit={validateForm}>
                      <h2 style={{ textAlign: 'left' }}>Login</h2>
                      <div className="form-group row">
                        <label htmlFor="userSelect" className="col-sm-2 col-form-label">User</label>
                        <div className="col-sm-11">
                          <select
                            id="userSelect"
                            className="form-control"
                            value={selectedUserId}
                            onChange={(e) => setSelectedUserId(e.target.value)}
                          >
                            <option value="">Select a user</option>
                            {users.map(user => (
                              <option key={user.id} value={user.id}>
                                {user.name}
                              </option>
                            ))}
                          </select>
                        </div>
                      </div>
                      <button type="submit" className="form-control btn btn-dark">Login</button>
                    </form>
                    <Link to="/register" className="form-control btn btn-light">Register</Link>
                    <br />
                    <br />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      <footer className="w-100 text-center mt-auto">
        <p>Author: Vidhya V<br />
          <a href="mailto:vidhya-v@pluralsight.com">vidhya-v@pluralsight.com</a>
        </p>
        <div className="copyright-area">
          <div className="container">
            <div className="row">
              <div className="col-md-12">
                <p className="text-center"> &copy; 2024 - All rights reserved </p>
              </div>
            </div>
          </div>
        </div>
      </footer>
    </div>
  );
};

export default Login;
