import React, { useEffect, useState } from 'react';
import 'bootstrap-icons/font/bootstrap-icons.css';

const Sidebar = ({ todos }) => {
  const [categories, setCategories] = useState([]);
  const [counts, setCounts] = useState({
    completed: 0,
    pending: 0,
    high: 0,
    medium: 0,
    low: 0,
    total: 0
  });

  useEffect(() => {
    fetchCategories();
    calculateCounts(todos);
  }, [todos]);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://localhost:8083/api/categories');
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const calculateCounts = (todos) => {
    const newCounts = {
      completed: 0,
      pending: 0,
      high: 0,
      medium: 0,
      low: 0,
      total: 0
    };

    const categoryCounts = {};

    todos.forEach(todo => {
      if (todo.completed) {
        newCounts.completed++;
      } else {
        newCounts.pending++;
      }

      switch (todo.priority.toLowerCase()) {
        case 'high':
          newCounts.high++;
          break;
        case 'medium':
          newCounts.medium++;
          break;
        case 'low':
          newCounts.low++;
          break;
        default:
          break;
      }

      if (!categoryCounts[todo.category]) {
        categoryCounts[todo.category] = 0;
      }
      categoryCounts[todo.category]++;
      newCounts.total++;
    });

    const updatedCategories = categories.map(category => ({
      ...category,
      count: categoryCounts[category.name] || 0
    }));

    setCounts(newCounts);
    setCategories(updatedCategories);
  };

  return (
    <div className="sidenav">
      <div className="d-flex">
        <button className="toggle-btn" type="button">
          <img src="stay-organized-react/src/logo.png" width="30" height="30" className="d-inline-block align-top" alt="" />
        </button>
        <div className="sidebar-logo">
          <h4 className="mb-4">Hello <span id="username1"></span></h4>
        </div>
      </div>
      <ul className="sidebar-nav">
        <li className="sidebar-item">
          <a href="snew-todo.html" className="sidebar-link">
            <i className="lni bi-plus-circle-fill"></i>
            <span>Create New Task</span>
          </a>
          <a href="suser-todos.html" className="sidebar-link">
            <i className="lni bi-binoculars-fill"></i>
            <span>View All User Tasks</span>
          </a>
        </li>
      </ul>
      <div className="todo-aside-categories-list">
        <span className="todo-aside-list-title">
          <i className="lni lni-list"></i> Categories
        </span>
        <ul className="todo-aside-list" id="todo-allCategory">
          <li className="todo-aside-sublist">Total Tasks &nbsp;&nbsp;
            <span>
              <span id="todo-alltask-count" className="badge text-bg-secondary todo_countBadge">{counts.total}</span>
            </span>
          </li>
          {categories.map((category) => (
            <li key={category.id} className="todo-aside-sublist">
              {category.name} &nbsp;&nbsp;
              <span>
                <span className="badge text-bg-secondary todo_countBadge">{category.count}</span>
              </span>
            </li>
          ))}
        </ul>
        <div id="category_listerror"></div>
      </div>
      <hr />
      <div className="todo-aside-status-list">
        <span className="todo-aside-list-title"> Status</span>
        <ul className="todo-aside-list">
          <li className="todo-aside-sublist">
            <i className="bi bi-patch-check-fill text-success" id="completedIcon"></i>&nbsp;Finished&nbsp;&nbsp;
            <span>
              <span id="todo-completed-count" className="badge text-bg-secondary todo_countBadge">{counts.completed}</span>
            </span>
          </li>
          <li className="todo-aside-sublist">
            <i className="bi bi-shield-fill-exclamation text-danger"></i>&nbsp;Pending&nbsp;&nbsp;
            <span>
              <span id="todo-pending-count" className="badge text-bg-secondary todo_countBadge">{counts.pending}</span>
            </span>
          </li>
        </ul>
      </div>
      <hr />
      <div className="todo-aside-priority-list">
        <span className="todo-aside-list-title"> Priority</span>
        <ul className="todo-aside-list">
          <li className="todo-aside-sublist">
            <i className="bi bi-thermometer-high text-danger"></i>&nbsp;High&nbsp;&nbsp;
            <span>
              <span id="todo-high-count" className="badge text-bg-secondary todo_countBadge">{counts.high}</span>
            </span>
          </li>
          <li className="todo-aside-sublist">
            <i className="bi bi-medium text-warning"></i>&nbsp;Medium&nbsp;&nbsp;
            <span>
              <span id="todo-medium-count" className="badge text-bg-secondary todo_countBadge">{counts.medium}</span>
            </span>
          </li>
          <li className="todo-aside-sublist">
            <i className="bi bi-thermometer-low text-success"></i>&nbsp;Low&nbsp;&nbsp;
            <span>
              <span id="todo-low-count" className="badge text-bg-secondary todo_countBadge">{counts.low}</span>
            </span>
          </li>
        </ul>
      </div>
      <a href="./landingpage.html" className="sidebar-link">
        <i className="lni lni-exit"></i>
        <span>Logout</span>
      </a>
    </div>
  );
};

export default Sidebar;
