import React, { useReducer, useEffect, useState } from 'react';
import ToDo from './Todo';
import Modal from 'react-modal';
import Sidebar from './Sidebar';

// Reducer function to manage state transitions
const reducer = (state, action) => {
    switch (action.type) {
        case 'ADD_TASK':
            return {
                ...state,
                userTasks: [...state.userTasks, action.payload]
            };
        case 'REMOVE_TASK':
            return {
                ...state,
                userTasks: state.userTasks.filter(task => task.id !== action.payload)
            };
        case 'EDIT_TASK':
            return {
                ...state,
                userTasks: state.userTasks.map(task =>
                    task.id === action.payload.id ? action.payload : task
                )
            };
        case 'SET_USERS':
            return {
                ...state,
                users: action.payload
            };
        case 'SET_USER_TASKS':
            return {
                ...state,
                userTasks: action.payload
            };
        default:
            return state;
    }
};

const ToDoList = () => {
    const initialState = {
        users: [],
        userTasks: []
    };

    const [state, dispatch] = useReducer(reducer, initialState);
    const [selectedUser, setSelectedUser] = useState('');
    const [modalIsOpen, setModalIsOpen] = useState(false);
    const [newTask, setNewTask] = useState({
        description: '',
        category: '',
        deadline: '',
        priority: 'Medium'
    });

    useEffect(() => {
        fetch('http://localhost:8083/api/users')
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'SET_USERS', payload: data });
            })
            .catch(error => console.error('Error fetching users:', error));
    }, []);

    const openModal = () => {
        setModalIsOpen(true);
    };

    const closeModal = () => {
        setModalIsOpen(false);
        setNewTask({
            description: '',
            category: '',
            deadline: '',
            priority: 'Medium'
        });
    };

    const handleAddTask = () => {
        if (!newTask.description || !newTask.category || !newTask.deadline || !selectedUser) return;
        const task = {
            ...newTask,
            userid: selectedUser,
            completed: false
        };

        fetch(`http://localhost:8083/api/todos`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(task)
        })
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'ADD_TASK', payload: data });
                closeModal();
            })
            .catch(err => {
                console.error('Error adding task:', err);
            });
    };

    const handleRemove = (id) => {
        fetch(`http://localhost:8083/api/todos/${id}`, {
            method: 'DELETE'
        })
            .then(() => {
                dispatch({ type: 'REMOVE_TASK', payload: id });
            })
            .catch(err => {
                console.error('Error removing task:', err);
            });
    };

    const handleUserChange = (e) => {
        const userId = e.target.value;
        setSelectedUser(userId);

        if (userId) {
            fetch(`http://localhost:8083/api/todos/byuser/${userId}`)
                .then(response => response.json())
                .then(data => {
                    dispatch({ type: 'SET_USER_TASKS', payload: data });
                })
                .catch(err => {
                    console.error('Error fetching todos:', err);
                });
        } else {
            dispatch({ type: 'SET_USER_TASKS', payload: [] });
        }
    };

    const handleNewTaskChange = (e) => {
        const { name, value } = e.target;
        setNewTask(prevState => ({
            ...prevState,
            [name]: value
        }));
    };

    return (
        <div className="todo-app">
            <Sidebar selectedUser={selectedUser} />
            <div className="content">
                <h1>TODO List</h1>
                <select value={selectedUser} onChange={handleUserChange}>
                    <option value="">Select User</option>
                    {state.users.map((user, index) => (
                        <option key={index} value={user.id}>{user.name}</option>
                    ))}
                </select>
                <button onClick={openModal}>Add Task</button>
                <ul>
                    {state.userTasks.map((task) => (
                        <ToDo
                            key={task.id}
                            task={task}
                            handleToggle={() => dispatch({ type: 'TOGGLE_TASK', payload: task.id })}
                            handleRemove={() => handleRemove(task.id)}
                        />
                    ))}
                </ul>
                {state.userTasks.length > 0 && (
                    <div id="todo-cards">
                        {state.userTasks.map((task) => (
                            <div key={task.id} className="todo-card">
                                <h3>{task.description}</h3>
                                <p>Category: {task.category}</p>
                                <p>Deadline: {task.deadline}</p>
                                <p>Priority: {task.priority}</p>
                                <p>Completed: {task.completed ? 'Yes' : 'No'}</p>
                            </div>
                        ))}
                    </div>
                )}
                <Modal isOpen={modalIsOpen} onRequestClose={closeModal}>
                    <h2>Add Task</h2>
                    <form>
                        <label>
                            Description:
                            <input
                                type="text"
                                name="description"
                                value={newTask.description}
                                onChange={handleNewTaskChange}
                            />
                        </label>
                        <label>
                            Category:
                            <input
                                type="text"
                                name="category"
                                value={newTask.category}
                                onChange={handleNewTaskChange}
                            />
                        </label>
                        <label>
                            Deadline:
                            <input
                                type="date"
                                name="deadline"
                                value={newTask.deadline}
                                onChange={handleNewTaskChange}
                            />
                        </label>
                        <label>
                            Priority:
                            <select
                                name="priority"
                                value={newTask.priority}
                                onChange={handleNewTaskChange}
                            >
                                <option value="Low">Low</option>
                                <option value="Medium">Medium</option>
                                <option value="High">High</option>
                            </select>
                        </label>
                    </form>
                    <button onClick={handleAddTask}>Add Task</button>
                    <button onClick={closeModal}>Cancel</button>
                </Modal>
            </div>
        </div>
    );
};

export default ToDoList;
