import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import 'bootstrap/dist/css/bootstrap.min.css';
import logo from '../images/logo1.png'; //logo
import loginpic1 from '../images/loginpic1.svg'; 
import '../App.css';

const Login = () => {
  const [users, setUsers] = useState([]);
  const [selectedUserId, setSelectedUserId] = useState('');
  const [password, setPassword] = useState(''); // New state for password
  const navigate = useNavigate();

  useEffect(() => {
    fetch("http://localhost:8083/api/users")
      .then(response => response.json())
      .then(data => setUsers(data))
      .catch(error => console.error('Error fetching users:', error));
  }, []);

  const validateForm = (event) => {
    event.preventDefault();
    const selectedUserName = users.find(user => user.id === selectedUserId)?.name;

    // Check if both user and password are selected
    if (selectedUserId && password) {
      alert(`Login successful! Welcome, ${selectedUserName}!`);
      navigate(`/dashboard`);
    } else {
      alert("Please select a user and enter a password.");
    }
  };

  return (
    <div className="min-vh-100 d-flex flex-column justify-content-center align-items-center">
      {/* Existing code */}
      <div className="container">
        <div className="row">
          <div className="col-md-6">
            <div className="mt-5">
            {/* Existing code */}

            </div>
          </div>
          <div className="col-md-6">
            <div className="col-md-10 mx-auto rounded shadow bg-white">
              <div className="row">
                <div className="col-md-12">
                  <div className="m-5 text-center">
                    <form className="m-5" onSubmit={validateForm}>
                      <h2 style={{ textAlign: 'left' }}>Login</h2>
                      <div className="form-group row">
                        <label htmlFor="userSelect" className="col-sm-2 col-form-label">User</label>
                        <div className="col-sm-11">
                          <select
                            id="userSelect"
                            className="form-control"
                            value={selectedUserId}
                            onChange={(e) => setSelectedUserId(e.target.value)}
                          >
                            <option value="">Select a user</option>
                            {users.map(user => (
                              <option key={user.id} value={user.id}>
                                {user.name}
                              </option>
                            ))}
                          </select>
                        </div>
                      </div>
                      {/* New password input field */}
                      <div className="form-group row">
                        <label htmlFor="passwordInput" className="col-sm-2 col-form-label">Password</label>
                        <div className="col-sm-10">
                          <input
                            type="password"
                            className="form-control"
                            id="passwordInput"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                          />
                        </div>
                      </div>
                      <button type="submit" className="form-control btn btn-dark">Login</button>
                    </form>
                    <Link to="/register" className="form-control btn btn-light">Register</Link>
                    <br />
                    <br />
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      {/* Existing code */}
    </div>
  );
};

export default Login;
