import React, { useReducer, useEffect, useState } from 'react';
import ToDo from './Todo';

// Reducer function to manage state transitions
const reducer = (state, action) => {
    switch (action.type) {
        case 'ADD_TASK':
            return {
                ...state,
                tasks: [...state.tasks, { taskName: action.payload, completed: false }]
            };
        case 'REMOVE_TASK':
            return {
                ...state,
                tasks: state.tasks.filter((_, index) => index !== action.payload)
            };
        case 'TOGGLE_TASK':
            return {
                ...state,
                tasks: state.tasks.map((task, index) =>
                    index === action.payload ? { ...task, completed: !task.completed } : task
                )
            };
        case 'RESET_TASKS':
            return {
                ...state,
                tasks: []
            };
        case 'SET_USERS':
            return {
                ...state,
                users: action.payload
            };
        case 'SET_USER_TASKS':
            return {
                ...state,
                userTasks: action.payload
            };
        default:
            return state;
    }
};

const ToDoList = () => {
    const initialState = {
        input: '',
        tasks: JSON.parse(localStorage.getItem('tasks')) || [],
        users: [], // New state to hold fetched usernames
        userTasks: [] // New state to hold tasks of the selected user
    };

    const [state, dispatch] = useReducer(reducer, initialState);
    const [input, setInput] = useState(initialState.input);
    const [selectedUser, setSelectedUser] = useState('');

    useEffect(() => {
        // Fetch usernames from API
        fetch('http://localhost:8083/api/users')
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'SET_USERS', payload: data });
            })
            .catch(error => console.error('Error fetching users:', error));
    }, []);

    useEffect(() => {
        localStorage.setItem('tasks', JSON.stringify(state.tasks));
    }, [state.tasks]);

    const handleAdd = () => {
        if (!input) return;
        dispatch({ type: 'ADD_TASK', payload: input });
        setInput('');
    };

    const handleRemove = (index) => {
        dispatch({ type: 'REMOVE_TASK', payload: index });
    };

    const handleToggle = (index) => {
        dispatch({ type: 'TOGGLE_TASK', payload: index });
    };

    const handleReset = () => {
        dispatch({ type: 'RESET_TASKS' });
    };

    const handleUserChange = (e) => {
        const username = e.target.value;
        setSelectedUser(username);

        if (username) {
            fetch(`http://localhost:8083/api/todos/byuser/${username}`)
                .then(response => response.json())
                .then(data => {
                    dispatch({ type: 'SET_USER_TASKS', payload: data });
                })
                .catch(err => {
                    console.error('Error fetching todos:', err);
                });
        } else {
            dispatch({ type: 'SET_USER_TASKS', payload: [] });
        }
    };

    return (
        <div>
            <h1>TODO List</h1>
            <h3>Add Task below</h3>
            <input value={input} onChange={(e) => setInput(e.target.value)} />
            <button onClick={handleAdd}>Add</button>
            {state.tasks.length > 0 && <button onClick={handleReset}>Reset</button>}
            <select value={selectedUser} onChange={handleUserChange}>
                <option value="">Select User</option>
                {state.users.map((user, index) => (
                    <option key={index} value={user.username}>{user.name}</option>
                ))}
            </select>
            <ul>
                {state.tasks.map((task, index) => (
                    <ToDo
                        key={index}
                        index={index}
                        task={task}
                        handleToggle={handleToggle}
                        handleRemove={handleRemove}
                    />
                ))}
            </ul>
            {state.userTasks.length > 0 && (
                <div id="todo-cards">
                    {state.userTasks.map((task, index) => (
                        <div key={index} className="todo-card">
                            <h3>{task.description}</h3>
                            <p>Category: {task.category}</p>
                            <p>Deadline: {task.deadline}</p>
                            <p>Priority: {task.priority}</p>
                            <p>Completed: {task.completed ? 'Yes' : 'No'}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default ToDoList;
