import React, { useReducer, useEffect, useState } from 'react';
import TodoCard from './TodoCard';

// Reducer function to manage state transitions
const reducer = (state, action) => {
    switch (action.type) {
        case 'ADD_TASK':
            return {
                ...state,
                userTasks: [...state.userTasks, action.payload]
            };
        case 'REMOVE_TASK':
            return {
                ...state,
                userTasks: state.userTasks.filter(task => task.id !== action.payload)
            };
        case 'EDIT_TASK':
            return {
                ...state,
                userTasks: state.userTasks.map(task =>
                    task.id === action.payload.id ? action.payload : task
                )
            };
        case 'SET_USERS':
            return {
                ...state,
                users: action.payload
            };
        case 'SET_USER_TASKS':
            return {
                ...state,
                userTasks: action.payload
            };
        default:
            return state;
    }
};

const ToDoList = () => {
    const initialState = {
        input: '',
        users: [],
        userTasks: []
    };

    const [state, dispatch] = useReducer(reducer, initialState);
    const [input, setInput] = useState(initialState.input);
    const [selectedUser, setSelectedUser] = useState('');
    const [editTask, setEditTask] = useState(null);
    const [filter, setFilter] = useState('ALL');
    const [priorityFilter, setPriorityFilter] = useState('ALL');
    const [searchQuery, setSearchQuery] = useState('');
    const [categories, setCategories] = useState([]); // New state for categories

    useEffect(() => {
        // Fetch usernames from API
        fetch('http://localhost:8083/api/users')
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'SET_USERS', payload: data });
            })
            .catch(error => console.error('Error fetching users:', error));

        // Fetch categories from API
        fetch('http://localhost:8083/api/categories')
            .then(response => response.json())
            .then(data => {
                setCategories(data);
            })
            .catch(error => console.error('Error fetching categories:', error));
    }, []);

    const handleAdd = () => {
        if (!input || !selectedUser) return;
        const newTask = {
            userid: selectedUser,
            description: input,
            category: categories[0].name, // Default to first category
            deadline: "2024-12-31",
            priority: "Medium",
            completed: false,
            categoryColor: "primary"
        };

        fetch(`http://localhost:8083/api/todos`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newTask)
        })
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'ADD_TASK', payload: data });
                setInput('');
            })
            .catch(err => {
                console.error('Error adding task:', err);
            });
    };

    const handleRemove = (id) => {
        fetch(`http://localhost:8083/api/todos/${id}`, {
            method: 'DELETE'
        })
            .then(() => {
                dispatch({ type: 'REMOVE_TASK', payload: id });
            })
            .catch(err => {
                console.error('Error removing task:', err);
            });
    };

    const handleEdit = () => {
        if (!editTask || !editTask.description) return;

        fetch(`http://localhost:8083/api/todos/${editTask.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(editTask)
        })
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'EDIT_TASK', payload: data });
                setEditTask(null);
            })
            .catch(err => {
                console.error('Error editing task:', err);
            });
    };

    const handleUserChange = (e) => {
        const userId = e.target.value;
        setSelectedUser(userId);

        if (userId) {
            fetch(`http://localhost:8083/api/todos/byuser/${userId}`)
                .then(response => response.json())
                .then(data => {
                    dispatch({ type: 'SET_USER_TASKS', payload: data });
                })
                .catch(err => {
                    console.error('Error fetching todos:', err);
                });
        } else {
            dispatch({ type: 'SET_USER_TASKS', payload: [] });
        }
    };

    const handleEditChange = (e) => {
        setEditTask({ ...editTask, [e.target.name]: e.target.value });
    };

    const handleFilterChange = (e) => {
        setFilter(e.target.value);
    };

    const handlePriorityFilterChange = (e) => {
        setPriorityFilter(e.target.value);
    };

    const handleSearchChange = (e) => {
        setSearchQuery(e.target.value);
    };

    const filteredTasks = state.userTasks.filter(task => {
        const statusMatch = (filter === 'ALL') || (filter === 'COMPLETED' && task.completed) || (filter === 'PENDING' && !task.completed);
        const priorityMatch = (priorityFilter === 'ALL') || (task.priority === priorityFilter);
        const searchMatch = task.description.toLowerCase().includes(searchQuery.toLowerCase());
        return statusMatch && priorityMatch && searchMatch;
    });

    return (
        <div>
            <h1>TODO List</h1>
            <h3>Add Task below</h3>
            <input value={input} onChange={(e) => setInput(e.target.value)} />
            <button onClick={handleAdd}>Add</button>
            {editTask && (
                <div>
                    <h3>Edit Task</h3>
                    <form onSubmit={(e) => { e.preventDefault(); handleEdit(); }}>
                        <input
                            name="description"
                            value={editTask.description}
                            onChange={handleEditChange}
                            placeholder="Description"
                        />
                        <select
                            name="category"
                            value={editTask.category}
                            onChange={handleEditChange}
                        >
                            {categories.map((category, index) => (
                                <option key={index} value={category.name}>{category.name}</option>
                            ))}
                        </select>
                        <input
                            type="date"
                            name="deadline"
                            value={editTask.deadline}
                            onChange={handleEditChange}
                        />
                        <select
                            name="priority"
                            value={editTask.priority}
                            onChange={handleEditChange}
                        >
                            <option value="Low">Low</option>
                            <option value="Medium">Medium</option>
                            <option value="High">High</option>
                        </select>
                        <select
                            name="completed"
                            value={editTask.completed}
                            onChange={handleEditChange}
                        >
                            <option value={false}>Pending</option>
                            <option value={true}>Completed</option>
                        </select>
                        <button type="submit">Save</button>
                    </form>
                </div>
            )}
            <select value={selectedUser} onChange={handleUserChange}>
                <option value="">Select User</option>
                {state.users.map((user, index) => (
                    <option key={index} value={user.id}>{user.name}</option>
                ))}
            </select>
            <select value={filter} onChange={handleFilterChange}>
                <option value="ALL">ALL</option>
                <option value="COMPLETED">Completed</option>
                <option value="PENDING">Pending</option>
            </select>
            <select value={priorityFilter} onChange={handlePriorityFilterChange}>
                <option value="ALL">ALL</option>
                <option value="Low">Low</option>
                <option value="Medium">Medium</option>
                <option value="High">High</option>
            </select>
            <input 
                type="text" 
                value={searchQuery} 
                onChange={handleSearchChange} 
                placeholder="Search tasks" 
            />
            <div id="todo-cards">
                {filteredTasks.map((task) => (
                    <TodoCard
                        key={task.id}
                        task={task}
                        handleToggle={(id) => dispatch({ type: 'TOGGLE_TASK', payload: id })}
                        handleRemove={handleRemove}
                        handleEdit={setEditTask}
                    />
                ))}
            </div>
        </div>
    );
};

export default ToDoList;
