// src/components/TodoList.js
import React, { useState, useEffect } from 'react';
//import 'bootstrap-icons/font/bootstrap-icons.css';

const TodoList = () => {
    const [todos, setTodos] = useState([]);
    const [username, setUsername] = useState('');
    const [categories, setCategories] = useState([]);
    const [stats, setStats] = useState({
        completed: 0,
        pending: 0,
        high: 0,
        medium: 0,
        low: 0,
        personal: 0,
        household: 0,
        financial: 0,
        work: 0,
        errand: 0,
        help: 0,
        total: 0
    });
    const [error, setError] = useState('');

    useEffect(() => {
        fetchUsers();
        fetchCategories();
    }, []);

    const fetchUsers = () => {
        fetch('http://localhost:8083/api/users')
            .then(response => response.json())
            .then(data => {
                if (data.length > 0) {
                    setUsername(data[0].id);
                    fetchTodos(data[0].id);
                }
            })
            .catch(err => setError("Error fetching user details from API"));
    };

    const fetchCategories = () => {
        fetch('http://localhost:8083/api/categories')
            .then(response => response.json())
            .then(data => setCategories(data))
            .catch(err => setError("Error fetching categories from API"));
    };

    const fetchTodos = (username) => {
        fetch(`http://localhost:8083/api/todos/byuser/${username}`)
            .then(response => response.json())
            .then(data => {
                setTodos(data);
                updateStats(data);
            })
            .catch(err => setError("Error fetching todos from API"));
    };

    const updateStats = (data) => {
        const newStats = {
            completed: 0,
            pending: 0,
            high: 0,
            medium: 0,
            low: 0,
            personal: 0,
            household: 0,
            financial: 0,
            work: 0,
            errand: 0,
            help: 0,
            total: data.length
        };
        data.forEach(todo => {
            if (todo.completed) newStats.completed++;
            else newStats.pending++;

            if (todo.priority === 'High') newStats.high++;
            else if (todo.priority === 'Medium') newStats.medium++;
            else newStats.low++;

            switch (todo.category) {
                case 'Personal Task': newStats.personal++; break;
                case 'Household Task': newStats.household++; break;
                case 'Financial Task': newStats.financial++; break;
                case 'Help Others': newStats.help++; break;
                case 'Errand': newStats.errand++; break;
                default: newStats.work++; break;
            }
        });
        setStats(newStats);
    };

    const handleUserChange = (event) => {
        const username = event.target.value;
        setUsername(username);
        fetchTodos(username);
    };

    const createCard = (todo) => {
        return (
            <div className="card todo-card" key={todo.id}>
                <div className="card-header todo-category-header">
                    <p>
                        <span className="todo-category-title todo-category-color-dot" id={`todo-category-${todo.id}`}></span>
                        <span className="todo-category-title">&nbsp;&nbsp;{todo.category}&nbsp;&nbsp;</span>
                        <span className="badge" id={`todo-priority-${todo.id}`}>{todo.priority}</span>
                        <span className="todo-icons">
                            <i className="bi bi-pencil-fill" id={`todo-complete-icon-${todo.id}`} data-bs-toggle="modal" data-bs-target="#exampleModal"></i>
                        </span>
                    </p>
                </div>
                <div className="card-body todo-body">
                    <div className="row">
                        <div className="col-9">
                            <h6 className="card-title todo-desc" id={`todo-desc-${todo.id}`}>{todo.description}</h6>
                        </div>
                        <div className="col-3">
                            <a><h6 className="todo-status" id={`todo-status-${todo.id}`}>{todo.completed ? 'Completed' : 'Pending'}</h6></a>
                        </div>
                    </div>
                    <p className="card-text" id={`todo-deadline-${todo.id}`}><i className="bi bi-calendar-heart"></i>&nbsp;&nbsp;Due date: {todo.deadline}</p>
                </div>
            </div>
        );
    };

    return (
        <div>
            <div>
                <select value={username} onChange={handleUserChange}>
                    {/* Populate the select options with usernames */}
                </select>
                {error && <div className="alert alert-danger">{error}</div>}
            </div>
            <div id="todo-cards">
                {todos.map(todo => createCard(todo))}
            </div>
        </div>
    );
};

export default TodoList;
