import React, { useState, useEffect } from 'react';

const Sidebar = ({ selectedUser }) => {
  const [categories, setCategories] = useState([]);
  const [counts, setCounts] = useState({
    completed_count: 0,
    pending_count: 0,
    high_count: 0,
    medium_count: 0,
    low_count: 0,
    personal_count: 0,
    household_count: 0,
    financial_count: 0,
    work_count: 0,
    errand_count: 0,
    help_count: 0,
    total: 0
  });

  useEffect(() => {
    if (selectedUser) {
      fetchCategories();
      fetchTodos(selectedUser);
    }
  }, [selectedUser]);

  const fetchCategories = async () => {
    try {
      const response = await fetch('http://localhost:8083/api/categories');
      const data = await response.json();
      setCategories(data);
    } catch (error) {
      console.error('Error fetching categories:', error);
    }
  };

  const fetchTodos = async (user) => {
    try {
      const response = await fetch(`http://localhost:8083/api/todos?user=${user}`);
      const data = await response.json();
      calculateCounts(data);
    } catch (error) {
      console.error('Error fetching todos:', error);
    }
  };

  const calculateCounts = (todos) => {
    const newCounts = {
      completed_count: 0,
      pending_count: 0,
      high_count: 0,
      medium_count: 0,
      low_count: 0,
      personal_count: 0,
      household_count: 0,
      financial_count: 0,
      work_count: 0,
      errand_count: 0,
      help_count: 0,
      total: 0
    };

    todos.forEach(todo => {
      if (todo.completed) {
        newCounts.completed_count++;
      } else {
        newCounts.pending_count++;
      }

      switch (todo.priority.toLowerCase()) {
        case 'high':
          newCounts.high_count++;
          break;
        case 'medium':
          newCounts.medium_count++;
          break;
        case 'low':
          newCounts.low_count++;
          break;
        default:
          break;
      }

      switch (todo.category.toLowerCase()) {
        case 'personal task':
          newCounts.personal_count++;
          break;
        case 'household task':
          newCounts.household_count++;
          break;
        case 'financial task':
          newCounts.financial_count++;
          break;
        case 'work task':
          newCounts.work_count++;
          break;
        case 'errand':
          newCounts.errand_count++;
          break;
        case 'help others':
          newCounts.help_count++;
          break;
        default:
          break;
      }

      newCounts.total++;
    });

    setCounts(newCounts);
  };

  return (
    <div className="sidenav">
      <div className="d-flex">
        <button className="toggle-btn" type="button">
          <img src="stay-organized-react/src/logo.png" width="30" height="30" className="d-inline-block align-top" alt="" />
        </button>
        <div className="sidebar-logo">
          <h4 className="mb-4">Hello <span id="username1">{selectedUser}</span></h4>
        </div>
      </div>
      <ul className="sidebar-nav">
        <li className="sidebar-item">
          <a href="snew-todo.html" className="sidebar-link">
            <i className="lni bi-plus-circle-fill"></i>
            <span>Create New Task</span>
          </a>
          <a href="suser-todos.html" className="sidebar-link">
            <i className="lni bi-binoculars-fill"></i>
            <span>View All User Tasks</span>
          </a>
        </li>
      </ul>
      <div className="todo-aside-categories-list">
        <span className="todo-aside-list-title">
          <i className="lni lni-list"></i> Categories
        </span>
        <ul className="todo-aside-list" id="todo-allCategory">
          <li className="todo-aside-sublist">Total Tasks &nbsp;&nbsp;
            <span>
              <span id="todo-alltask-count" className="badge text-bg-secondary todo_countBadge">{counts.total}</span>
            </span>
          </li>
          {categories.map((category) => (
            <li key={category.id} className="todo-aside-sublist">
              {category.name} &nbsp;&nbsp;
              <span>
                <span className="badge text-bg-secondary todo_countBadge">{counts[`${category.name.toLowerCase().replace(' ', '_')}_count`]}</span>
              </span>
            </li>
          ))}
        </ul>
        <div id="category_listerror"></div>
      </div>
      <hr />
      <div className="todo-aside-status-list">
        <span className="todo-aside-list-title"> Status</span>
        <ul className="todo-aside-list">
          <li className="todo-aside-sublist">
            <i className="bi bi-patch-check-fill text-success" id="completedIcon"></i>&nbsp;Finished&nbsp;&nbsp;
            <span>
              <span id="todo-completed-count" className="badge text-bg-secondary todo_countBadge">{counts.completed_count}</span>
            </span>
          </li>
          <li className="todo-aside-sublist">
            <i className="bi bi-shield-fill-exclamation text-danger"></i>&nbsp;Pending&nbsp;&nbsp;
            <span>
              <span id="todo-pending-count" className="badge text-bg-secondary todo_countBadge">{counts.pending_count}</span>
            </span>
          </li>
        </ul>
      </div>
      <hr />
      <div className="todo-aside-priority-list">
        <span className="todo-aside-list-title"> Priority</span>
        <ul className="todo-aside-list">
          <li className="todo-aside-sublist">
            <i className="bi bi-thermometer-high text-danger"></i>&nbsp;High&nbsp;&nbsp;
            <span>
              <span id="todo-high-count" className="badge text-bg-secondary todo_countBadge">{counts.high_count}</span>
            </span>
          </li>
          <li className="todo-aside-sublist">
            <i className="bi bi-medium text-warning"></i>&nbsp;Medium&nbsp;&nbsp;
            <span>
              <span id="todo-medium-count" className="badge text-bg-secondary todo_countBadge">{counts.medium_count}</span>
            </span>
          </li>
          <li className="todo-aside-sublist">
            <i className="bi bi-thermometer-low text-success"></i>&nbsp;Low&nbsp;&nbsp;
            <span>
              <span id="todo-low-count" className="badge text-bg-secondary todo_countBadge">{counts.low_count}</span>
            </span>
          </li>
        </ul>
      </div>
      <a href="./landingpage.html" className="sidebar-link">
        <i className="lni lni-exit"></i>
        <span>Logout</span>
      </a>
    </div>
  );
};

export default Sidebar;
