import React, { useReducer, useEffect, useState } from 'react';
import ToDo from './Todo';

// Reducer function to manage state transitions
const reducer = (state, action) => {
    switch (action.type) {
        case 'ADD_TASK':
            return {
                ...state,
                userTasks: [...state.userTasks, action.payload]
            };
        case 'REMOVE_TASK':
            return {
                ...state,
                userTasks: state.userTasks.filter(task => task.id !== action.payload)
            };
        case 'EDIT_TASK':
            return {
                ...state,
                userTasks: state.userTasks.map(task =>
                    task.id === action.payload.id ? action.payload : task
                )
            };
        case 'RESET_TASKS':
            return {
                ...state,
                tasks: []
            };
        case 'SET_USERS':
            return {
                ...state,
                users: action.payload
            };
        case 'SET_USER_TASKS':
            return {
                ...state,
                userTasks: action.payload
            };
        default:
            return state;
    }
};

const ToDoList = () => {
    const initialState = {
        input: '',
        tasks: JSON.parse(localStorage.getItem('tasks')) || [],
        users: [], // New state to hold fetched usernames
        userTasks: [] // New state to hold tasks of the selected user
    };

    const [state, dispatch] = useReducer(reducer, initialState);
    const [input, setInput] = useState(initialState.input);
    const [selectedUser, setSelectedUser] = useState('');
    const [editTask, setEditTask] = useState(null);

    useEffect(() => {
        // Fetch usernames from API
        fetch('http://localhost:8083/api/users')
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'SET_USERS', payload: data });
            })
            .catch(error => console.error('Error fetching users:', error));
    }, []);

    useEffect(() => {
        localStorage.setItem('tasks', JSON.stringify(state.tasks));
    }, [state.tasks]);

    const handleAdd = () => {
        if (!input || !selectedUser) return;
        const newTask = {
            id: Date.now(), // This is a placeholder. In a real app, the ID would be generated by the backend.
            userid: selectedUser,
            description: input,
            category: "General", // Default category, can be modified as needed
            deadline: "2024-12-31", // Default deadline, can be modified as needed
            priority: "Medium", // Default priority, can be modified as needed
            completed: false
        };

        // Assuming the backend expects a POST request to add a new task
        fetch(`http://localhost:8083/api/todos`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newTask)
        })
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'ADD_TASK', payload: data });
                setInput('');
            })
            .catch(err => {
                console.error('Error adding task:', err);
            });
    };

    const handleRemove = (id) => {
        // Assuming the backend expects a DELETE request to remove a task
        fetch(`http://localhost:8083/api/todos/${id}`, {
            method: 'DELETE'
        })
            .then(() => {
                dispatch({ type: 'REMOVE_TASK', payload: id });
            })
            .catch(err => {
                console.error('Error removing task:', err);
            });
    };

    const handleEdit = () => {
        if (!editTask || !editTask.description) return;

        // Assuming the backend expects a PUT request to update a task
        fetch(`http://localhost:8083/api/todos/${editTask.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(editTask)
        })
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'EDIT_TASK', payload: data });
                setEditTask(null);
                setInput('');
            })
            .catch(err => {
                console.error('Error editing task:', err);
            });
    };

    const handleUserChange = (e) => {
        const userId = e.target.value;
        setSelectedUser(userId);

        if (userId) {
            fetch(`http://localhost:8083/api/todos/byuser/${userId}`)
                .then(response => response.json())
                .then(data => {
                    dispatch({ type: 'SET_USER_TASKS', payload: data });
                })
                .catch(err => {
                    console.error('Error fetching todos:', err);
                });
        } else {
            dispatch({ type: 'SET_USER_TASKS', payload: [] });
        }
    };

    const handleEditChange = (e) => {
        setEditTask({ ...editTask, description: e.target.value });
    };

    return (
        <div>
            <h1>TODO List</h1>
            <h3>Add Task below</h3>
            <input value={input} onChange={(e) => setInput(e.target.value)} />
            <button onClick={handleAdd}>Add</button>
            {editTask && (
                <div>
                    <h3>Edit Task</h3>
                    <input value={editTask.description} onChange={handleEditChange} />
                    <button onClick={handleEdit}>Save</button>
                </div>
            )}
            {state.tasks.length > 0 && <button onClick={handleReset}>Reset</button>}
            <select value={selectedUser} onChange={handleUserChange}>
                <option value="">Select User</option>
                {state.users.map((user, index) => (
                    <option key={index} value={user.id}>{user.name}</option>
                ))}
            </select>
            <ul>
                {state.userTasks.map((task) => (
                    <ToDo
                        key={task.id}
                        task={task}
                        handleToggle={() => dispatch({ type: 'TOGGLE_TASK', payload: task.id })}
                        handleRemove={() => handleRemove(task.id)}
                        handleEdit={() => setEditTask(task)}
                    />
                ))}
            </ul>
            {state.userTasks.length > 0 && (
                <div id="todo-cards">
                    {state.userTasks.map((task) => (
                        <div key={task.id} className="todo-card">
                            <h3>{task.description}</h3>
                            <p>Category: {task.category}</p>
                            <p>Deadline: {task.deadline}</p>
                            <p>Priority: {task.priority}</p>
                            <p>Completed: {task.completed ? 'Yes' : 'No'}</p>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default ToDoList;
