import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const NewTodoForm = () => {
    const [users, setUsers] = useState([]);
    const [categories, setCategories] = useState([]);
    const [userId, setUserId] = useState('');
    const [category, setCategory] = useState('');
    const [description, setDescription] = useState('');
    const [deadline, setDeadline] = useState('');
    const [priority, setPriority] = useState('Low');
    const [showTaskDetails, setShowTaskDetails] = useState(false);
    const [addedTodo, setAddedTodo] = useState(null);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);

    const navigate = useNavigate();

    useEffect(() => {
        // Fetch users and categories
        const fetchData = async () => {
            try {
                const [usersResponse, categoriesResponse] = await Promise.all([
                    fetch("http://localhost:8083/api/users"),
                    fetch("http://localhost:8083/api/categories")
                ]);

                const usersData = await usersResponse.json();
                const categoriesData = await categoriesResponse.json();

                setUsers(usersData);
                setCategories(categoriesData);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching data:', error);
                setError('Failed to fetch data. Please try again later.');
                setLoading(false);
            }
        };

        fetchData();
    }, []);

    const addNewTodo = async () => {
        if (!userId || !category || !description || !deadline) {
            setError('All fields are required.');
            return;
        }

        const todoData = {
            userId,
            category,
            description,
            deadline,
            priority,
            completed: false
        };

        try {
            const response = await fetch("http://localhost:8083/api/todos", {
                method: "POST",
                body: JSON.stringify(todoData),
                headers: {
                    "Content-type": "application/json; charset=UTF-8"
                }
            });

            const todo = await response.json();
            setAddedTodo(todo);
            setShowTaskDetails(true);
        } catch (error) {
            console.error('Error adding todo:', error);
            setError('Failed to add todo. Please try again later.');
        }
    };

    if (loading) {
        return <div>Loading...</div>;
    }

    return (
        <main>
            <div className="container container-form">
                <h4>Create New Task</h4>
                {error && <div className="alert alert-danger">{error}</div>}
                <form>
                    {/* ... existing form fields ... */}
                    <div className="mb-3">
                        <button type="button" className="form-control btn btn-dark" onClick={addNewTodo}>Add Task</button>
                    </div>
                </form>
                {showTaskDetails && addedTodo && (
                    <div className="card text-black mb-3">
                        {/* ... existing code for displaying task details ... */}
                    </div>
                )}
            </div>
        </main>
    );
};

export default NewTodoForm;
