import React, { useReducer, useEffect, useState } from 'react';
import TodoCard from './TodoCard';
import Sidebar from './Sidebar';
import UserDropdown from './UserDropdown';
import NewTaskForm from './NewTaskForm';
import './StickyNotes.css'; // Import the CSS file
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faThumbtack, faPlus } from '@fortawesome/free-solid-svg-icons'; // Import the required icons
import TodoImg from '../images/Todoimg.png';

// Reducer function to manage state transitions
const reducer = (state, action) => {
    switch (action.type) {
        case 'ADD_TASK':
            return {
                ...state,
                userTasks: [...state.userTasks, action.payload]
            };
        case 'REMOVE_TASK':
            return {
                ...state,
                userTasks: state.userTasks.filter(task => task.id !== action.payload)
            };
        case 'EDIT_TASK':
            return {
                ...state,
                userTasks: state.userTasks.map(task =>
                    task.id === action.payload.id ? action.payload : task
                )
            };
        case 'SET_USERS':
            return {
                ...state,
                users: action.payload
            };
        case 'SET_USER_TASKS':
            return {
                ...state,
                userTasks: action.payload
            };
        case 'TOGGLE_TASK':
            return {
                ...state,
                userTasks: state.userTasks.map(task =>
                    task.id === action.payload ? { ...task, completed: !task.completed } : task
                )
            };
        default:
            return state;
    }
};

const ToDoList = () => {
    const initialState = {
        input: '',
        category: '', // New state for category
        deadline: '', // New state for deadline
        priority: '', // New state for priority
        users: [],
        userTasks: []
    };

    const [state, dispatch] = useReducer(reducer, initialState);
    const [input, setInput] = useState(initialState.input);
    const [selectedUser, setSelectedUser] = useState('');
    const [editTask, setEditTask] = useState(null);
    const [filter, setFilter] = useState('ALL');
    const [priorityFilter, setPriorityFilter] = useState('ALL');
    const [searchQuery, setSearchQuery] = useState('');
    const [categories, setCategories] = useState([]);
    const [userCounts, setUserCounts] = useState({});
    const [sortOrder, setSortOrder] = useState('NONE'); // New state for sorting order
    const [deadline, setDeadline] = useState(''); // State for deadline
    const [priority, setPriority] = useState(''); 
    const [category, setCategory] = useState(''); // State for category
    const [showForm, setShowForm] = useState(false);

    useEffect(() => {
        fetch('http://localhost:8083/api/users')
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'SET_USERS', payload: data });
            })
            .catch(error => console.error('Error fetching users:', error));

        fetch('http://localhost:8083/api/categories')
            .then(response => response.json())
            .then(data => {
                setCategories(data);
            })
            .catch(error => console.error('Error fetching categories:', error));
    }, []);

    useEffect(() => {
        if (selectedUser) {
            fetch(`http://localhost:8083/api/todos/byuser/${selectedUser}`)
                .then(response => response.json())
                .then(data => {
                    dispatch({ type: 'SET_USER_TASKS', payload: data });
                    calculateCounts(data);
                })
                .catch(err => {
                    console.error('Error fetching todos:', err);
                });
        } else {
            dispatch({ type: 'SET_USER_TASKS', payload: [] });
            calculateCounts([]);
        }
    }, [selectedUser]);

    const handleInputChange = (e) => {
        setInput(e.target.value);
    };

    const handleCategoryChange = (e) => {
        setCategory(e.target.value);
    };

    const handleDeadlineChange = (e) => {
        setDeadline(e.target.value);
    };

    const handlePriorityChange = (e) => {
        setPriority(e.target.value);
    };

    const calculateCounts = (todos) => {
        const counts = {
            completed: 0,
            pending: 0,
            high: 0,
            medium: 0,
            low: 0,
            personal: 0,
            household: 0,
            financial: 0,
            work: 0,
            errand: 0,
            help: 0,
            total: 0
        };

        todos.forEach(todo => {
            if (todo.completed) {
                counts.completed++;
            } else {
                counts.pending++;
            }

            switch (todo.priority.toLowerCase()) {
                case 'high':
                    counts.high++;
                    break;
                case 'medium':
                    counts.medium++;
                    break;
                case 'low':
                    counts.low++;
                    break;
                default:
                    break;
            }

            switch (todo.category.toLowerCase()) {
                case 'personal task':
                    counts.personal++;
                    break;
                case 'household task':
                    counts.household++;
                    break;
                case 'financial task':
                    counts.financial++;
                    break;
                case 'work task':
                    counts.work++;
                    break;
                case 'errand':
                    counts.errand++;
                    break;
                case 'help others':
                    counts.help++;
                    break;
                default:
                    break;
            }

            counts.total++;
        });

        setUserCounts(counts);
    };

    const handleAdd = (task) => {
        if (!task.description || !selectedUser || !task.category || !task.deadline || !task.priority) return;
        const newTask = {
            userid: selectedUser,
            description: task.description,
            category: task.category,
            deadline: task.deadline,
            priority: task.priority,
            completed: false,
            categoryColor: "primary"
        };

        fetch(`http://localhost:8083/api/todos`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(newTask)
        })
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'ADD_TASK', payload: data });
                calculateCounts([...state.userTasks, data]);
            })
            .catch(err => {
                console.error('Error adding task:', err);
            });
    };

    const handleRemove = (id) => {
        fetch(`http://localhost:8083/api/todos/${id}`, {
            method: 'DELETE'
        })
            .then(() => {
                dispatch({ type: 'REMOVE_TASK', payload: id });
                calculateCounts(state.userTasks.filter(task => task.id !== id));
            })
            .catch(err => {
                console.error('Error removing task:', err);
            });
    };

    const handleEdit = () => {
        if (!editTask || !editTask.description) return;

        fetch(`http://localhost:8083/api/todos/${editTask.id}`, {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(editTask)
        })
            .then(response => response.json())
            .then(data => {
                dispatch({ type: 'EDIT_TASK', payload: data });
                setEditTask(null);
                calculateCounts(state.userTasks.map(task => task.id === data.id ? data : task));
            })
            .catch(err => {
                console.error('Error editing task:', err);
            });
    };

    const handleToggle = (id) => {
        const task = state.userTasks.find(task => task.id === id);
        if (task) {
            fetch(`http://localhost:8083/api/todos/${id}`, {
                method: 'PUT',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({ ...task, completed: !task.completed })
            })
                .then(response => response.json())
                .then(data => {
                    dispatch({ type: 'EDIT_TASK', payload: data });
                    calculateCounts(state.userTasks.map(task => task.id === data.id ? data : task));
                })
                .catch(err => {
                    console.error('Error toggling task:', err);
                });
        }
    };

    const handleUserChange = (e) => {
        const userId = e.target.value;
        setSelectedUser(userId);
    };

    const handleSortOrderChange = (order) => {
        setSortOrder(order);
    };

    const handleCancelEdit = () => {
        setEditTask(null);
    };

    const filteredTasks = state.userTasks.filter(task => {
        if (filter === 'COMPLETED' && !task.completed) {
            return false;
        }
        if (filter === 'PENDING' && task.completed) {
            return false;
        }
        if (priorityFilter !== 'ALL' && task.priority !== priorityFilter) {
            return false;
        }
        if (searchQuery && !task.description.toLowerCase().includes(searchQuery.toLowerCase())) {
            return false;
        }
        return true;
    });

    return (
        <div className="container-fluid">
            <div className="row">
                <Sidebar />
                <div className="col-md-10">
                    <h1 className="my-4">Todo List</h1>
                    <UserDropdown users={state.users} selectedUser={selectedUser} onUserChange={handleUserChange} />

                    {selectedUser && (
                        <>
                            <div className="row mb-3">
                                <div className="col-md-6">
                                    <input
                                        type="text"
                                        className="form-control"
                                        placeholder="Search tasks"
                                        value={searchQuery}
                                        onChange={(e) => setSearchQuery(e.target.value)}
                                    />
                                </div>
                                <div className="col-md-3">
                                    <select className="form-control" value={priorityFilter} onChange={(e) => setPriorityFilter(e.target.value)}>
                                        <option value="ALL">All Priorities</option>
                                        <option value="Low">Low</option>
                                        <option value="Medium">Medium</option>
                                        <option value="High">High</option>
                                    </select>
                                </div>
                                <div className="col-md-3">
                                    <select className="form-control" value={filter} onChange={(e) => setFilter(e.target.value)}>
                                        <option value="ALL">All Tasks</option>
                                        <option value="PENDING">Pending</option>
                                        <option value="COMPLETED">Completed</option>
                                    </select>
                                </div>
                            </div>
                            <div className="center-container">
                                <div className="btn-group mb-3">
                                    <button 
                                        className={`btn ${sortOrder === 'NONE' ? 'btn-primary' : 'btn-secondary'}`} 
                                        onClick={() => handleSortOrderChange('NONE')}
                                    >
                                        None
                                    </button>
                                    <button 
                                        className={`btn ${sortOrder === 'ASC' ? 'btn-primary' : 'btn-secondary'}`} 
                                        onClick={() => handleSortOrderChange('ASC')}
                                    >
                                        Ascending
                                    </button>
                                    <button 
                                        className={`btn ${sortOrder === 'DESC' ? 'btn-primary' : 'btn-secondary'}`} 
                                        onClick={() => handleSortOrderChange('DESC')}
                                    >
                                        Descending
                                    </button>
                                </div>
                            </div>
                        </>
                    )}

                    {selectedUser && (
                        <div className="sticky-notes-container">
                            <div className="sticky-note">
                                <FontAwesomeIcon icon={faThumbtack} className="thumbtack" />
                                <h3>Pending item Priority Counts:</h3>
                                <ul>
                                    <li>Low: {state.userTasks.filter(task => !task.completed && task.priority === 'Low').length}</li>
                                    <li>Medium: {state.userTasks.filter(task => !task.completed && task.priority === 'Medium').length}</li>
                                    <li>High: {state.userTasks.filter(task => !task.completed && task.priority === 'High').length}</li>
                                </ul>
                            </div>
                            <div className="sticky-note">
                                <FontAwesomeIcon icon={faThumbtack} className="thumbtack" />
                                <h3>User status Counts:</h3>
                                <ul>
                                    <li>Pending Tasks: {userCounts.pending}</li>
                                    <li>Completed Tasks: {userCounts.completed}</li>
                                </ul>
                            </div>
                            <div className="sticky-note">
                                <FontAwesomeIcon icon={faThumbtack} className="thumbtack" />
                                <h3>Category-wise Counts:</h3>
                                <ul>
                                    <li>Personal Tasks: {userCounts.personal}</li>
                                    <li>Household Tasks: {userCounts.household}</li>
                                    <li>Financial Tasks: {userCounts.financial}</li>
                                    <li>Work Tasks: {userCounts.work}</li>
                                    <li>Errands: {userCounts.errand}</li>
                                    <li>Help Others: {userCounts.help}</li>
                                </ul>
                            </div>
                        </div>
                    )}

                    {!showForm ? (
                        <div className="center-container">
                            <button onClick={() => setShowForm(true)} className="btn btn-primary mb-3">
                                <FontAwesomeIcon icon={faPlus} /> Add Task
                            </button>
                        </div>
                    ) : null}

                    {showForm && (
                        <NewTaskForm 
                            input={input}
                            onInputChange={handleInputChange}
                            onCategoryChange={handleCategoryChange}
                            onDeadlineChange={handleDeadlineChange}
                            onPriorityChange={handlePriorityChange}
                            onSubmit={handleAdd}
                            categories={categories}
                            deadline={deadline}
                            priority={priority}
                            category={category}
                        />
                    )}

                    <div className="row">
                        {filteredTasks.length > 0 ? (
                            filteredTasks.map(task => (
                                <TodoCard 
                                    key={task.id}
                                    task={task}
                                    onRemove={handleRemove}
                                    onToggle={handleToggle}
                                    onEdit={handleEdit}
                                    onCancelEdit={handleCancelEdit}
                                    isEditing={editTask && editTask.id === task.id}
                                    onEditChange={setEditTask}
                                />
                            ))
                        ) : (
                            <p>No tasks found.</p>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ToDoList;
